import React ,{useState} from 'react';
import Srclist from './Srclist';

const Search = () => {
    const [img ,setImg] = useState();

     const inputEvent = (e) => {
      const data = e.target.value;
      setImg(data);

     }

    return(

        <>  <div className='inp'>
            <input type="text" placeholder='search anything' 
              value ={img}
                onChange= {inputEvent}
              />
            </div>
         {img === ""?null: <Srclist name= {img}/>}   
        </>
    )
}
export default Search ;
