import React, {useState} from "react";



const Content = () =>{
    const [item ,setItem]=useState();

       
    const  getValue = (e) => {
      
       setItem (e.target.value)



    }
    
       

    return (
        <>
            <h1>to do list</h1>
            <br/>
            <input type="text" placeholder="add item" onChange={getValue}  />
            <button>+</button>
            <br/>
            <h2>{item}</h2>
           
        </>

    )
}
export default Content;
